using System.Collections; using System.Collections.Generic; using UnityEngine;  public class PurplePlayerControl : MonoBehaviour {     public float moveSpeed;     [HideInInspector]     public Rigidbody2D myRigidbody;     private Vector3 moveDirection;      private bool chase;     public Transform Soccerball;     private float SoccerballDistance;     public float chaseSpeed;      // Use this for initialization     void Start()     {         myRigidbody = GetComponent<Rigidbody2D>();      }      // Update is called once per frame     void Update()     {         SoccerballDistance = Vector2.Distance(Soccerball.position, transform.position);
        lookAtSoccerball();     }      void lookAtSoccerball()     {         transform.position = Vector2.MoveTowards(transform.position, Soccerball.position, chaseSpeed * Time.deltaTime);     } }